// ═══════════════════════════════════════════════════════════
// 🎯 SÉLECTION DU PROMPT DE BRIEFING
// ═══════════════════════════════════════════════════════════
// Ce nœud utilise les prompts récupérés depuis GitHub via l'API
// et permet la sélection manuelle du type de briefing
// ═══════════════════════════════════════════════════════════

const inputItems = $input.all();
const data = inputItems[0].json;

// ═══════════════════════════════════════════════════════════
// RÉCUPÉRATION DES PROMPTS DEPUIS "Fetch Prompts from API"
// ═══════════════════════════════════════════════════════════
// Les prompts viennent de "Fetch Prompts from API" qui précède "Get Active Tickers"
// L'API retourne { success: true, prompts: { morning: {...}, midday: {...}, evening: {...} } }
let promptsConfig = {};

try {
  // Méthode 1 : Récupérer depuis le nœud "Fetch Prompts from API"
  const fetchPromptsData = $('Fetch Prompts from API').first().json;
  if (fetchPromptsData.prompts) {
    promptsConfig = fetchPromptsData.prompts;
    console.log('✅ Prompts récupérés depuis Fetch Prompts from API');
  } else if (fetchPromptsData.success && fetchPromptsData.prompts) {
    promptsConfig = fetchPromptsData.prompts;
    console.log('✅ Prompts récupérés depuis Fetch Prompts from API (format success)');
  }
} catch (e) {
  console.warn('⚠️ Impossible de récupérer depuis Fetch Prompts from API:', e.message);
}

// Méthode 2 : Vérifier dans les données actuelles (au cas où)
if (!promptsConfig || Object.keys(promptsConfig).length === 0) {
  if (data.prompts) {
    promptsConfig = data.prompts;
    console.log('✅ Prompts trouvés dans les données actuelles');
  } else if (data.success && data.prompts) {
    promptsConfig = data.prompts;
    console.log('✅ Prompts trouvés dans les données actuelles (format success)');
  }
}

// Si toujours pas de prompts, erreur
if (!promptsConfig || Object.keys(promptsConfig).length === 0) {
  console.error('❌ Prompts non trouvés. Vérifiez que "Fetch Prompts from API" s\'est exécuté correctement.');
  throw new Error('❌ Les prompts depuis GitHub ne sont pas disponibles. Vérifiez le nœud "Fetch Prompts from API".');
}

// ═══════════════════════════════════════════════════════════
// PRIORITÉ 1 : Prompt personnalisé (si fourni)
// ═══════════════════════════════════════════════════════════
if (data.custom_prompt && data.custom_prompt.trim() !== '') {
  console.log('✅ Utilisation du prompt personnalisé');
  for (const item of inputItems) {
    item.json.selected_prompt = data.custom_prompt;
    item.json.prompt_type = 'custom';
    item.json.processing_status = "OK";
  }
  return inputItems;
}

// ═══════════════════════════════════════════════════════════
// PRIORITÉ 2 : Type de briefing sélectionné manuellement
// ═══════════════════════════════════════════════════════════
// Note: prompt_type est généré automatiquement à partir de briefing_type
if (data.briefing_type) {
  const selectedType = data.briefing_type.toLowerCase().trim();

  // ═══════════════════════════════════════════════════════════
  // CONVERSION FRANÇAIS → ANGLAIS (pour compatibilité API)
  // ═══════════════════════════════════════════════════════════
  const typeMapping = {
    // Français → Anglais
    'matin': 'morning',
    'midi': 'midday',
    'soir': 'evening',
    // Anglais (compatibilité)
    'morning': 'morning',
    'midday': 'midday',
    'evening': 'evening',
    'noon': 'midday' // Ancien format
  };

  // Normaliser le type (français ou anglais → anglais)
  let normalizedType = typeMapping[selectedType];

  if (!normalizedType) {
    throw new Error(`❌ Type de briefing invalide: "${selectedType}". Utilisez: "matin", "midi", "soir" (ou "morning", "midday", "evening")`);
  }

  // Types valides pour l'API (toujours en anglais)
  const validTypes = ['morning', 'midday', 'evening'];
  if (!validTypes.includes(normalizedType)) {
    throw new Error(`❌ Type de briefing invalide après conversion: ${normalizedType}`);
  }

  const selectedPrompt = promptsConfig[normalizedType]?.prompt;

  if (!selectedPrompt) {
    throw new Error(`❌ Prompt non trouvé pour le type: ${normalizedType}. Vérifiez config/briefing-prompts.json sur GitHub.`);
  }

  console.log(`✅ Utilisation du prompt ${normalizedType} (${selectedType}) depuis GitHub`);

  for (const item of inputItems) {
    item.json.selected_prompt = selectedPrompt;
    item.json.prompt_type = normalizedType; // ✅ Généré automatiquement depuis briefing_type
    item.json.processing_status = "OK";
    item.json.briefing_name = promptsConfig[normalizedType]?.name || `Briefing ${normalizedType}`;
    // Conserver le type original pour affichage
    item.json.briefing_type_original = selectedType;
  }

  return inputItems;
}

// ═══════════════════════════════════════════════════════════
// PRIORITÉ 3 : Détermination automatique selon l'heure (Schedule Trigger)
// ═══════════════════════════════════════════════════════════
const now = new Date();
const hour = now.getHours();

let promptType;
let selectedPrompt;

if (hour >= 5 && hour < 11) {
  // Morning prompt (5am - 11am)
  promptType = 'morning';
  selectedPrompt = promptsConfig.morning?.prompt || '';
} else if (hour >= 11 && hour < 16) {
  // Midday prompt (11am - 4pm)
  promptType = 'midday';
  selectedPrompt = promptsConfig.midday?.prompt || '';
} else {
  // Evening prompt (4pm onwards and before 5am)
  promptType = 'evening';
  selectedPrompt = promptsConfig.evening?.prompt || '';
}

if (!selectedPrompt) {
  throw new Error(`❌ Prompt non trouvé pour le type: ${promptType}`);
}

console.log(`✅ Détermination automatique: ${promptType} (heure: ${hour}h)`);

for (const item of inputItems) {
  item.json.selected_prompt = selectedPrompt;
  item.json.prompt_type = promptType;
  item.json.processing_status = "OK";
  item.json.briefing_name = promptsConfig[promptType]?.name || `Briefing ${promptType}`;
}

return inputItems;

