// ═══════════════════════════════════════════════════════════
// 📊 EXTRACT TITLE AND SUBTITLE
// ═══════════════════════════════════════════════════════════
function extractTitleAndSubtitle(content) {
  const lines = content.split('\n');
  let title = '';
  let subtitle = '';

  // Extract title from first # or ##
  for (const line of lines) {
    if (line.trim().startsWith('# ') && !title) {
      title = line.replace(/^#\s+/, '').trim();
      break;
    } else if (line.trim().startsWith('## ') && !title) {
      title = line.replace(/^##\s+/, '').trim();
      break;
    }
  }

  // Extract subtitle from first paragraph
  for (const line of lines) {
    if (line.trim() && !line.startsWith('#') && !line.startsWith('*') && !line.startsWith('-') && line.length > 30) {
      subtitle = line.trim().substring(0, 150);
      if (subtitle.length === 150) subtitle += '...';
      break;
    }
  }

  // Fallback titles
  if (!title) {
    if (content.toLowerCase().includes('marchés') || content.toLowerCase().includes('marché')) {
      title = 'Analyse des Marchés Financiers';
    } else if (content.toLowerCase().includes('action')) {
      title = 'Revue des Actions et Titres';
    } else if (content.toLowerCase().includes('économie')) {
      title = 'Perspectives Économiques';
    } else {
      title = 'Briefing Financier Emma IA';
    }
  }

  if (!subtitle) {
    subtitle = 'Analyse détaillée et insights pour investisseurs avertis';
  }

  return { title, subtitle };
}

