function getContextualGreeting(triggerType) {
  const greetings = {
    'morning': '🌅 Bonjour et bienvenue à votre briefing matinal',
    'midday': '☀️ Bon après-midi, voici votre briefing du midi',
    'noon': '☀️ Bon après-midi, voici votre briefing du midi',
    'evening': '🌆 Bonsoir, découvrez votre briefing du soir'
  };

  const greeting = greetings[triggerType];
  if (greeting) return greeting;

  // Fallback based on hour
  const hour = new Date().getHours();
  if (hour >= 5 && hour < 12) return '🌅 Bonjour et bienvenue à votre briefing matinal';
  if (hour >= 12 && hour < 18) return '☀️ Bon après-midi, voici votre briefing du midi';
  return '🌆 Bonsoir, découvrez votre briefing du soir';
}

// ═══════════════════════════════════════════════════════════
// 📊 EXTRACT KEY STATS FROM CONTENT
// ═══════════════════════════════════════════════════════════
function extractKeyStats(content) {
  const stats = [];

  // Look for common patterns: "S&P 500: +0.5%", "AAPL $150 +2%", etc.
  const patterns = [
    /S&P\s*500[:\s]+([+-]?\d+\.?\d*%)/i,
    /NASDAQ[:\s]+([+-]?\d+\.?\d*%)/i,
    /DOW[:\s]+([+-]?\d+\.?\d*%)/i,
    /VIX[:\s]+([\d.]+)/i
  ];

  patterns.forEach((pattern, idx) => {
    const match = content.match(pattern);
    if (match) {
      const labels = ['S&P 500', 'NASDAQ', 'DOW', 'VIX'];
      stats.push({ label: labels[idx], value: match[1] });
    }
  });

  return stats;
}

// ═══════════════════════════════════════════════════════════
// 🎯 EXTRACT TL;DR BULLET POINTS
// ═══════════════════════════════════════════════════════════
function extractTLDR(content) {
  const lines = content.split('\n');
  const bullets = [];

  // Look for first 3 important bullet points or sentences
  for (const line of lines) {
    const trimmed = line.trim();
    if ((trimmed.startsWith('•') || trimmed.startsWith('-') || trimmed.startsWith('*')) && trimmed.length > 20) {
      bullets.push(trimmed.replace(/^[•\-\*]\s*/, ''));
      if (bullets.length >= 3) break;
    }
  }

  // If not enough bullets, extract first 3 meaningful sentences
  if (bullets.length < 3) {
    const sentences = content.match(/[^.!?]+[.!?]+/g) || [];
    sentences.forEach(sentence => {
      if (bullets.length < 3 && sentence.length > 30 && sentence.length < 200) {
        bullets.push(sentence.trim());
      }
    });
  }

  return bullets.slice(0, 3);
}

